INSERT INTO `np_about` (`aid`, `type`, `title`, `content`) VALUES
(43, '硕士点', '15个硕士点', '<p>硕士点&nbsp;70304&nbsp;物理化学(含∶化学物理)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p>硕士点&nbsp;80102&nbsp;固体力学&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p>硕士点&nbsp;80103&nbsp;流体力学&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p>硕士点&nbsp;80104&nbsp;工程力学&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p>硕士点&nbsp;80105&nbsp;计算力学与仿真工程&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p>硕士点&nbsp;817&nbsp;&nbsp;&nbsp;&nbsp;应用化学&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p>硕士点&nbsp;81903&nbsp;安全技术及工程&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p>硕士点&nbsp;82601&nbsp;武器系统与运用工程&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p>硕士点&nbsp;82603&nbsp;火炮、自动武器与弹药工程&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p>硕士点&nbsp;82604&nbsp;军事化学与烟火技术</p>'),
(44, '博士点', '9个博士点', '<p>博士点&nbsp;&nbsp;力学</p><p>博士点&nbsp;&nbsp;工程力学&nbsp;</p><p>博士点&nbsp;&nbsp;固体力学</p><p>博士点&nbsp;&nbsp;液体力学&nbsp;&nbsp;&nbsp;&nbsp;</p><p>博士点&nbsp;&nbsp;兵器科学与技术&nbsp;&nbsp;&nbsp;&nbsp;</p><p>博士点&nbsp;&nbsp;武器系统与运用工程&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p>博士点&nbsp;&nbsp;火炮、自动武器与弹药工程&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p>博士点&nbsp;&nbsp;军事化学与烟火技术</p><p>博士点&nbsp;&nbsp;安全科学与工程&nbsp;</p><p>博士点&nbsp;&nbsp;材料科学与工程</p><p>博士点&nbsp;&nbsp;应用化学</p><p>博士点&nbsp;&nbsp;化学&nbsp;&nbsp;</p>'),
(45, '博士后流动站', '2个博士后流动站', '<p>博士后流动站&nbsp;801&nbsp;力学（可授工学、理学学位）&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p>博士后流动站&nbsp;826&nbsp;兵器科学与技术&nbsp;&nbsp;</p>'),
(25, '爆炸与毁伤力学', '爆炸与毁伤力学', '<p style="line-height:150%;text-indent:32px"><span style="line-height:150%;font-family:宋体;color:#000000">主要研究非理想爆轰理论，点火、起爆理论，爆炸动力学与应用，爆炸测试技术与计算方法，材料动态损伤与破坏，材料与结构的动态响应，固体宏细观力学行为等。</span><span style="line-height:150%;font-family:&#39;arial&#39;,&#39;sans-serif&#39;;color:red"></span></p><p style="text-indent:2em">&nbsp;</p>'),
(24, '含能材料理论与应用', '含能材料理论与应用', '<p style="line-height:150%;text-indent:32px"><span style="line-height:150%;font-family:宋体;color:black">主要研究含能材料分子结构与设计，含能化合物合成，高威力炸药、高能推进剂、火工烟火药剂理论与应用等。</span><span style="line-height:150%;font-family:&#39;arial&#39;,&#39;sans-serif&#39;;color:black"></span></p><p>&nbsp;</p>'),
(26, '防护理论与技术', '防护理论与技术', '<p style="line-height:150%;text-indent:32px"><span style="line-height:150%;font-family:宋体;color:black">主要研究爆炸与冲击作用下工事与装备的防护理论，新型防护材料与结构，防护效果评价等。</span><span style="line-height:150%;font-family:&#39;arial&#39;,&#39;sans-serif&#39;;color:black"></span></p><p style="text-indent:2em">&nbsp;</p>'),
(27, '毁伤理论及应用', '毁伤理论及应用', '<p style="line-height:150%;text-indent:32px"><span style="line-height:150%;font-family:宋体;color:black">主要研究爆炸毁伤机理与战斗部技术，新型毁伤机理与技术，目标易损性与生存能力，引爆控制理论与应用等。</span><span style="line-height:150%;font-family:&#39;arial&#39;,&#39;sans-serif&#39;;color:black"></span></p><p style="text-indent:2em">&nbsp;</p>'),
(28, '爆炸安全理论与技术', '爆炸安全理论与技术', '<p style="line-height:150%;text-indent:28px"><span style="line-height:150%;font-family:宋体;color:black">主要研究爆炸安全理论与应用，爆炸物探测与识别，燃爆系统可靠性与安全性评估等。</span><span style="line-height:150%;font-family:&#39;arial&#39;,&#39;sans-serif&#39;;color:black"></span></p><p style="text-indent:2em">&nbsp;</p>');
