INSERT INTO `np_article` (`aid`, `ord`, `type`, `title`, `content`, `add_date`, `add_time`, `add_user`) VALUES
(1, 1, 79, '爆炸科学与技术国家重点实验室第五届学术委员会第3次会议顺利召开', '<P><FONT style="FONT-SIZE: 16px">&nbsp;&nbsp;&nbsp; 爆炸科学与技术国家重点实验室第五届学术委员会第3次会议于2013年12月19日在北京理工大学召开，会议分别由学术委员会主任白以龙院士、副主任孙承纬院士和黄风雷教授主持。学术委员会12人参加了会议。会议的主要议题有：听取开放基金项目结题报告；审议2014年自主课题和开放基金项目的立项申请；讨论重点实验室发展相关事宜 。</FONT></P>\r\n<P><FONT size=3>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;<IMG style="WIDTH: 412px; HEIGHT: 308px" src="/labadmin/uploadfile/201312201387528539.jpg"></FONT></P><FONT size=3>\r\n<P>&nbsp;&nbsp;&nbsp; 王作山等24位开放基金项目人向学术委员会做了结题验收报告，经过学术委员会讨论、质询，通过了其中22个项目的结题验收，1个项目评为优秀并获准延续资助。 </P>\r\n<P>&nbsp;&nbsp;&nbsp; 会议审议并通过了庞磊等11人的2014年开放基金项目的立项申请，以及任慧等11人的2014年自主课题的立项申请。</P>\r\n<P>&nbsp;&nbsp;&nbsp; 会议还就重点实验室的发展进行了讨论。</P>\r\n<P>&nbsp;</P></FONT>', '2013-12-20', 1399988283, 'wangte'),
(2, 2, 79, '“爆炸科学与技术国家重点实验室（北京理工大学）山东研究基地”第一届理事会第4次会议召开', '<P><FONT style="FONT-SIZE: 16px">&nbsp;&nbsp;&nbsp; 2013年10月23日，爆炸科学与技术国家重点实验室（北京理工大学）山东研究基地在淄博召开了第一届理事会第4次会议。</FONT><A name=OLE_LINK2></A><A name=OLE_LINK1><FONT style="FONT-SIZE: 16px">山东特种工业集团</FONT></A><FONT style="FONT-SIZE: 16px">领导，北京理工大学机电学院和爆炸科学与技术国家重点实验室领导，理事会成员、特邀专家、科研专项基金项目负责人等参加了会议。</FONT></P>\r\n<P><FONT size=3>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<IMG style="WIDTH: 460px; HEIGHT: 305px" src="/labadmin/uploadfile/201310281382932127.jpg"></FONT></P>\r\n<P><FONT size=3>&nbsp;&nbsp;&nbsp; 山东研究基地副理事长、<A name=OLE_LINK6></A><A name=OLE_LINK5>山东特种工业集团</A>董事长陈建华代表集团公司致欢迎词，并就理事会一年来的工作进行了总结；山东研究基地办公室主任李学林向理事会汇报了基地建设情况；理事会成员就山东研究基地的建设与发展进行了热烈讨论，对山东研究基地科学研究重点方向、人才队伍建设、运行管理等方面提出了很好的意见。爆炸科学与技术国家重点实验室主任张庆明教授就山东研究基地下一步工作提出了建议。会议由山东特种工业集团副总经理杨守杰主持。</P>\r\n<P>&nbsp;&nbsp;&nbsp; 受爆炸科学与技术国家重点实验室学术委员会委托，会议成立了山东基地科研专项基金评审组，对申请项目的立项、结题项目的验收以及在研项目的进展进行了评审。2011和2012年度立项的科研专项基金项目，有些已列入总装备部预研专项、外贸型号研制和产品试制，有些在生产中得到了应用。</P>\r\n<P>&nbsp;&nbsp;&nbsp; 爆炸科学与技术国家重点实验室山东研究基地自2010年成立以来，强化了学校与企业的协同创新，为学校的科学研究与人才培养做了大量工作，同时也对企业的发展起到了明显的推动作用。 </P>\r\n<P>&nbsp;</P>\r\n<P>&nbsp;</P></FONT>', '2013-10-28', 1399988283, 'wangte'),
(3, 3, 79, '中国兵工学会爆炸与安全技术专业委员会第四届委员会第一次会议暨学术研讨会召开', '<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; WORD-BREAK: break-all; mso-char-indent-count: 2.0"><SPAN style="COLOR: #333333"><FONT style="FONT-SIZE: 16px">根据中国兵工学会《关于爆炸与安全技术专业委员会第四届委员会委员的批复》文件（兵学字[2013]51号），经中国兵工学会批准，中国兵工学会爆炸与安全技术专业委员会于<SPAN lang=EN-US>2013</SPAN>年10月<SPAN lang=EN-US>12-13</SPAN>日在浙江杭州召开了第四届专业委员会第一次会议，并进行了爆炸与安全技术学术研讨。本次会议由中国兵工学会爆炸与安全技术专业委员会主办，北京理工大学爆炸科学与技术国家重点实验室承办。<?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; WORD-BREAK: break-all; mso-char-indent-count: 2.0"><SPAN style="COLOR: #333333"><FONT style="FONT-SIZE: 16px">爆炸与安全技术专业委员会总干事刘彦教授主持了第四届委员会第一次会议，中国科协副主席冯长根教授、兵工学会组织与会员工作部孙岩副主任、爆炸科学与技术国家重点实验室主任张庆明教授分别代表中国科协、兵工学会、专委会挂靠单位对会议的召开表示祝贺。爆炸科学与技术国家重点实验室主任张庆明教授宣读了爆炸与安全技术专业委员会第四届专业委员会成员名单，兵工学会组织与会员工作部孙岩副主任为爆炸与安全技术专业委员会第四届委员会的名誉主任委员、主任委员、副主任委员、总干事颁发了聘书。爆炸与安全技术专业委员会主任委员冯顺山教授代表第四届委员会发表了讲话。总干事刘彦教授对爆炸与安全技术专业委员会的发展历程和任务宗旨进行了介绍，对近几年专委会的工作进行了汇报，并提出了专委会下一步的工作计划。<o:p></o:p></FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; WORD-BREAK: break-all; mso-char-indent-count: 2.0"><SPAN style="COLOR: #333333"><FONT style="FONT-SIZE: 16px">爆炸与安全技术专业委员会主任委员冯顺山教授、副主任委员陈鹏万教授主持了学术研讨会。会议邀请了北京理工大学冯长根教授、南京理工大学彭金华教授、中物院流体物理研究所祝文军研究员、北京理工大学冯顺山教授、国防科技大学张震宇教授、中北大学刘玉存教授、中物院总体工程研究所陈小伟研究员作了特邀报告。<o:p></o:p></FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; WORD-BREAK: break-all; mso-char-indent-count: 2.0"><SPAN style="COLOR: #333333"><FONT style="FONT-SIZE: 16px">会议决定2014年召开中国兵工学会第十一届爆炸与安全技术学术年会。</FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; WORD-BREAK: break-all; mso-char-indent-count: 2.0"><SPAN style="COLOR: #333333"></SPAN>&nbsp;</P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; WORD-BREAK: break-all; mso-char-indent-count: 2.0"><SPAN style="COLOR: #333333"></SPAN><SPAN style="COLOR: #333333"><FONT style="FONT-SIZE: 16px"><SPAN lang=EN-US><o:p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;<IMG style="WIDTH: 393px; HEIGHT: 262px" src="/labadmin/uploadfile/201310211382324601.jpg" width=395 height=194></o:p></SPAN></FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; WORD-BREAK: break-all; mso-char-indent-count: 2.0"><SPAN style="COLOR: #333333"><FONT style="FONT-SIZE: 16px"><SPAN lang=EN-US><o:p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <FONT style="FONT-SIZE: 16px"><FONT style="FONT-SIZE: 16px"><SPAN style="FONT-FAMILY: 宋体; COLOR: #333333; mso-font-kerning: 1.0pt; mso-bidi-font-family: Times New Roman; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA"><STRONG>大会现场</STRONG></SPAN><SPAN style="FONT-FAMILY: Times New Roman,serif; COLOR: #333333; mso-fareast-font-family: 宋体; mso-font-kerning: 1.0pt; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA" lang=EN-US><SPAN style="mso-spacerun: yes">&nbsp;&nbsp;&nbsp; </SPAN></SPAN></FONT></FONT></o:p></SPAN></FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; WORD-BREAK: break-all; mso-char-indent-count: 2.0"><SPAN style="COLOR: #333333"><FONT style="FONT-SIZE: 16px"><SPAN lang=EN-US><o:p><FONT style="FONT-SIZE: 16px"><SPAN style="FONT-FAMILY: Times New Roman,serif; COLOR: #333333; mso-fareast-font-family: 宋体; mso-font-kerning: 1.0pt; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA" lang=EN-US><SPAN style="mso-spacerun: yes">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; <IMG style="WIDTH: 393px; HEIGHT: 262px" src="/labadmin/uploadfile/201310211382324675.jpg" width=394 height=192></SPAN></SPAN></FONT></o:p></SPAN></FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; WORD-BREAK: break-all; mso-char-indent-count: 2.0"><SPAN style="COLOR: #333333"><FONT style="FONT-SIZE: 16px"><SPAN lang=EN-US><o:p><FONT style="FONT-SIZE: 16px"><SPAN style="FONT-FAMILY: Times New Roman,serif; COLOR: #333333; mso-fareast-font-family: 宋体; mso-font-kerning: 1.0pt; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA" lang=EN-US><SPAN style="mso-spacerun: yes">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<SPAN style="LINE-HEIGHT: 150%; COLOR: #333333; FONT-SIZE: 10.5pt"><STRONG><FONT style="FONT-SIZE: 16px"><FONT style="FONT-SIZE: 16px">刘彦教授主持专委会会议</FONT></FONT></STRONG></SPAN></SPAN></SPAN></FONT></o:p></SPAN></FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; WORD-BREAK: break-all; mso-char-indent-count: 2.0"><SPAN style="COLOR: #333333"><FONT style="FONT-SIZE: 16px"><SPAN lang=EN-US><o:p><FONT style="FONT-SIZE: 16px"><SPAN style="FONT-FAMILY: Times New Roman,serif; COLOR: #333333; mso-fareast-font-family: 宋体; mso-font-kerning: 1.0pt; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA" lang=EN-US><SPAN style="mso-spacerun: yes"><SPAN style="LINE-HEIGHT: 150%; COLOR: #333333; FONT-SIZE: 10.5pt"><STRONG><FONT style="FONT-SIZE: 16px"><FONT style="FONT-SIZE: 16px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <IMG style="WIDTH: 391px; HEIGHT: 261px" src="/labadmin/uploadfile/201310211382324786.jpg" width=392 height=203></FONT></FONT></STRONG></SPAN></SPAN></SPAN></FONT></o:p></SPAN></FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; WORD-BREAK: break-all; mso-char-indent-count: 2.0"><SPAN style="COLOR: #333333"><FONT style="FONT-SIZE: 16px"><SPAN lang=EN-US><o:p><FONT style="FONT-SIZE: 16px"><SPAN style="FONT-FAMILY: Times New Roman,serif; COLOR: #333333; mso-fareast-font-family: 宋体; mso-font-kerning: 1.0pt; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA" lang=EN-US><SPAN style="mso-spacerun: yes"><SPAN style="LINE-HEIGHT: 150%; COLOR: #333333; FONT-SIZE: 10.5pt"><STRONG><FONT style="FONT-SIZE: 16px"><FONT style="FONT-SIZE: 16px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <FONT style="FONT-SIZE: 16px">&nbsp;<SPAN style="FONT-FAMILY: 宋体; mso-font-kerning: 1.0pt; mso-bidi-font-family: Times New Roman; mso-ascii-font-family: Times New Roman; mso-hansi-font-family: Times New Roman; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA">冯长根教授讲话</SPAN><SPAN style="FONT-FAMILY: Times New Roman,serif; mso-fareast-font-family: 宋体; mso-font-kerning: 1.0pt; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA"> </SPAN></FONT></FONT></FONT></STRONG></SPAN></SPAN></SPAN></FONT></o:p></SPAN></FONT></SPAN></P>\r\n<P style="LINE-HEIGHT: 150%; TEXT-INDENT: 24pt; MARGIN: 0cm 0cm 0pt; WORD-BREAK: break-all; mso-char-indent-count: 2.0"><SPAN style="COLOR: #333333"><FONT style="FONT-SIZE: 16px"><SPAN lang=EN-US><o:p><FONT style="FONT-SIZE: 16px"><SPAN style="FONT-FAMILY: Times New Roman,serif; COLOR: #333333; mso-fareast-font-family: 宋体; mso-font-kerning: 1.0pt; mso-ansi-language: EN-