;\r\n	mso-font-alt:SimSun;\r\n	mso-font-charset:134;\r\n	mso-generic-font-family:auto;\r\n	mso-font-pitch:variable;\r\n	mso-font-signature:3 135135232 16 0 262145 0;}\r\n@font-face\r\n	{font-family:"@宋体";\r\n	panose-1:2 1 6 0 3 1 1 1 1 1;\r\n	mso-font-charset:134;\r\n	mso-generic-font-family:auto;\r\n	mso-font-pitch:variable;\r\n	mso-font-signature:3 135135232 16 0 262145 0;}\r\n /* Style Definitions */\r\n p.MsoNormal, li.MsoNormal, div.MsoNormal\r\n	{mso-style-parent:"";\r\n	margin:0cm;\r\n	margin-bottom:.0001pt;\r\n	text-align:justify;\r\n	text-justify:inter-ideograph;\r\n	mso-pagination:none;\r\n	font-size:10.5pt;\r\n	mso-bidi-font-size:12.0pt;\r\n	font-family:"Times New Roman";\r\n	mso-fareast-font-family:宋体;\r\n	mso-font-kerning:1.0pt;}\r\n /* Page Definitions */\r\n @page\r\n	{mso-page-border-surround-header:no;\r\n	mso-page-border-surround-footer:no;}\r\n@page Section1\r\n	{size:612.0pt 792.0pt;\r\n	margin:72.0pt 90.0pt 72.0pt 90.0pt;\r\n	mso-header-margin:36.0pt;\r\n	mso-footer-margin:36.0pt;\r\n	mso-paper-source:0;}\r\ndiv.Section1\r\n	{page:Section1;}\r\n-->\r\n</style><!--[if gte mso 10]>\r\n<style>\r\n /* Style Definitions */\r\n table.MsoNormalTable\r\n	{mso-style-name:普通表格;\r\n	mso-tstyle-rowband-size:0;\r\n	mso-tstyle-colband-size:0;\r\n	mso-style-noshow:yes;\r\n	mso-style-parent:"";\r\n	mso-padding-alt:0cm 5.4pt 0cm 5.4pt;\r\n	mso-para-margin:0cm;\r\n	mso-para-margin-bottom:.0001pt;\r\n	mso-pagination:widow-orphan;\r\n	font-size:10.0pt;\r\n	font-family:"Times New Roman";\r\n	mso-fareast-font-family:"Times New Roman";\r\n	mso-ansi-language:#0400;\r\n	mso-fareast-language:#0400;\r\n	mso-bidi-language:#0400;}\r\n</style>\r\n<![endif]-->\r\n\r\n<p class="MsoNormal" style="text-indent: 24pt;"><st1:chsdate isrocdate="False" islunardate="False" day="11" month="6" year="2010" w:st="on"><span style="font-size: 12pt;" lang="EN-US">6</span><span style="font-size: 12pt; font-family: 宋体;">月</span><span style="font-size: 12pt;" lang="EN-US">11</span><span style="font-size: 12pt; font-family: 宋体;">日</span></st1:chsdate><span style="font-size: 12pt; font-family: 宋体;">上午，加拿大多伦多大学土木工程系夏开文教授应邀来爆炸与科学国家重点实验室进行学术交流，并在重点实验室二层会议室作了题为“应用霍普金森杆技术研究岩石的拉伸破坏”的学术报告。会议由刘彦副教授主持，重点实验室武海军副教授、董永香副教授等部分师生参加了此次报告会。</span><span style="font-size: 12pt;" lang="EN-US"><o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-indent: 24pt;"><span style="font-size: 12pt; font-family: 宋体;">夏开文教授就霍普金森拉杆的工作原理及其在岩石爆破中工程中的应用等问题与在场师生进行了交流，并介绍了多伦多大学的实验室情况。夏教授的报告内容丰富生动，与现场师生之间良好的沟通互动，使与会师生受益匪浅。</span></p><p class="MsoNormal" style="text-indent: 24pt;"><span style="font-size: 12pt; font-family: 宋体;"><br></span></p><p class="MsoNormal" style="text-indent: 24pt; text-align: center;"><img style="width: 419px; height: 280px;" src="../uploadfile/201006111276243617.jpg"></p><p class="MsoNormal" style="text-indent: 24pt; text-align: center;"><meta http-equiv="Content-Type" content="text/html; charset=utf-8"><meta name="ProgId" content="Word.Document"><meta name="Generator" content="Microsoft Word 11"><meta name="Originator" content="Microsoft Word 11"><link rel="File-List" href="file:///C:%5CDOCUME%7E1%5Cuser%5CLOCALS%7E1%5CTemp%5Cmsohtml1%5C01%5Cclip_filelist.xml"><o:smarttagtype namespaceuri="urn:schemas-microsoft-com:office:smarttags" name="PersonName"></o:smarttagtype><!--[if gte mso 9]><xml>\r\n <w:WordDocument>\r\n  <w:View>Normal</w:View>\r\n  <w:Zoom>0</w:Zoom>\r\n  <w:PunctuationKerning/>\r\n  <w:DrawingGridVerticalSpacing>7.8 磅</w:DrawingGridVerticalSpacing>\r\n  <w:DisplayHorizontalDrawingGridEvery>0</w:DisplayHorizontalDrawingGridEvery>\r\n  <w:DisplayVerticalDrawingGridEvery>2</w:DisplayVerticalDrawingGridEvery>\r\n  <w:ValidateAgainstSchemas/>\r\n  <w:SaveIfXMLInvalid>false</w:SaveIfXMLInvalid>\r\n  <w:IgnoreMixedContent>false</w:IgnoreMixedContent>\r\n  <w:AlwaysShowPlaceholderText>false</w:AlwaysShowPlaceholderText>\r\n  <w:Compatibility>\r\n   <w:SpaceForUL/>\r\n   <w:BalanceSingleByteDoubleByteWidth/>\r\n   <w:DoNotLeaveBackslashAlone/>\r\n   <w: