INSERT INTO `np_article` (`aid`, `ord`, `type`, `title`, `content`, `add_date`, `add_time`, `add_user`) VALUES
(309, 309, 75, '第四届学术委员会第5次会议和2010年年度学术报告会暨全国强动载效应及防护冬季研讨会议程 ', '<IMG src="/labadmin/Edit/images/common.gif" align=absMiddle><A href="/labadmin/uploadfile/201012211292908933.doc" target=_blank>第四届学术委员会第5次会议和2010年年度学术报告会暨全国强动载效应及防护冬季研讨会议程</A> ', '2010-12-21', 1399989402, 'wangte'),
(310, 310, 75, '全国强动载效应及防护冬季研讨会在校学生录取名单', '<P>&nbsp;&nbsp;&nbsp; </P>\r\n<P>&nbsp;&nbsp;&nbsp; 经过讨论，以下学生可参加爆炸科学与技术国家重点实验室主办的<FONT face=宋体>全国强动载效应及防护冬季研讨会。由实验室统一安排住宿，特殊困难者可报销往返硬座车票。26日在北京理工大学北门附近的国际交流大厦报到，届时请务必带上学生证。目前尚有名额，希望各校研究生积极申请报名。</FONT></P>\r\n<P align=center>\r\n<TABLE style="WIDTH: 376.5pt; BORDER-COLLAPSE: collapse" height=1075 width=502>\r\n<COLGROUP>\r\n<COL width=72>\r\n<COL width=101>\r\n<COL width=329>\r\n<TBODY>\r\n<TR height=52>\r\n<TD class=et7 style="FONT-WEIGHT: 700; FONT-SIZE: 16pt; VERTICAL-ALIGN: middle; WIDTH: 183.75pt; COLOR: #000000; FONT-STYLE: normal; HEIGHT: 39pt; TEXT-ALIGN: center; TEXT-DECORATION: none; mso-protection: locked visible" width=245 colSpan=3 height=52>\r\n<P align=center>&nbsp;</P></TD></TR>\r\n<TR height=33>\r\n<TD class=et2 style="BORDER-RIGHT: #000000 0.5pt solid; BORDER-TOP: #000000 0.5pt solid; FONT-WEIGHT: 400; FONT-SIZE: 12pt; VERTICAL-ALIGN: middle; BORDER-LEFT: #000000 0.5pt solid; WIDTH: 54pt; COLOR: #000000; BORDER-BOTTOM: #000000 0.5pt solid; FONT-STYLE: normal; WHITE-SPACE: normal; HEIGHT: 24.75pt; TEXT-ALIGN: center; TEXT-DECORATION: none; mso-protection: locked visible" width=72 height=33>\r\n<P align=center>序号</P></TD>\r\n<TD class=et2 style="BORDER-RIGHT: #000000 0.5pt solid; BORDER-TOP: #000000 0.5pt solid; FONT-WEIGHT: 400; FONT-SIZE: 12pt; VERTICAL-ALIGN: middle; BORDER-LEFT: #000000 0.5pt solid; WIDTH: 75.75pt; COLOR: #000000; BORDER-BOTTOM: #000000 0.5pt solid; FONT-STYLE: normal; WHITE-SPACE: normal; HEIGHT: 24.75pt; TEXT-ALIGN: center; TEXT-DECORATION: none; mso-protection: locked visible" width=101 height=33>\r\n<P align=center>姓名<SPAN style="mso-spacerun: yes">&nbsp;</SPAN></P></TD>\r\n<TD class=et2 style="BORDER-RIGHT: #000000 0.5pt solid; BORDER-TOP: #000000 0.5pt solid; FONT-WEIGHT: 400; FONT-SIZE: 12pt; VERTICAL-ALIGN: middle; BORDER-LEFT: #000000 0.5pt solid; WIDTH: 246.75pt; COLOR: #000000; BORDER-BOTTOM: #000000 0.5pt solid; FONT-STYLE: normal; WHITE-SPACE: normal; HEIGHT: 24.75pt; TEXT-AL