(411, 411, 102, '联系我们', '<p style="line-height:150%;text-align:left;"><span style="font-family:宋体, simsun;font-size:14px;"></span></p><p>办公电话： &nbsp;010－68913957 &nbsp;010－68914267</p><p>传真电话： &nbsp;010－68914267</p><p>E-mail： &nbsp; &nbsp; &nbsp;nkles@bit.edu.cn &nbsp; wlqhq@bit.edu.cn</p><p>网 &nbsp; 站： &nbsp; &nbsp; &nbsp;http://est.bit.edu.cn </p><p><br /></p>', '2014-05-18', 1400379347, 'wangte'),
(401, 401, 42, '发表论文', '<p style="text-align:left;line-height:3em;margin-top:0px;margin-bottom:0px;"><br /></p><table width="100%" height="30PX" border="1" bordercolor="#000000"><tbody><tr><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle"><span style="font-size:16px;font-weight:bold;line-height:36px;">年度 </span><br /></td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">2005</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">2006</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">2007</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">2008</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">2009</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">2010</td></tr><tr><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle"><span style="font-size:16px;font-weight:bold;line-height:36px;">国外刊物</span><br /></td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">98</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">69</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">49</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">72</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">98</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">97</td></tr><tr><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle"><span style="font-size:16px;font-weight:bold;line-height:36px;">国内重要刊物</span><br /></td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">118</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">261</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">258</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">135</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">245</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">194</td></tr><tr><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle"><span style="font-family:&#39;times new roman&#39;;font-size:16px;font-weight:bold;line-height:36px;">三大收录数（含ISTP)</span><br /></td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">106</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">250</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">202</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">160</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">218</td><td style="width:14%;" bgcolor="#FFFFFF" align="center" valign="middle">217</td></tr></tbody></table><p><br /></p>', '2014-05-17', 1400333209, 'wangte'),
