INSERT INTO `np_direction` (`aid`, `type`, `title`, `content`) VALUES
(24, '含能材料理论与应用技术', '含能材料理论与应用技术', '<p style="text-indent:2em;">圣利来集团是一家涉及多个行业的多元化集团，集团总部位于北京市海淀区，旗下分公司及业务涉及房地产开发及物业管理、经营性租赁、机械制造及印刷产品制造等。</p><p style="text-indent:2em;">集团各项业务均由专业独立公司运作，其中甘肃海天房地产开发有限公司成立于2000年9月，是经甘肃省住房和城乡建设厅核定的具有二级资质等级的房地产开发企业，注册资本2000万元，办公地址位于兰州市城关区张掖路87号中广大厦九层，办公场地面积达1300多平方米，经营范围主要为房地产开发、商品房销售。</p><p style="text-indent:2em;">滦县圣利来包装有限公司成立于2011年总投资3亿元，位于滦县经济开发区，占地78亩，年产各类高档彩色包装纸箱、纸盒8万只，主要以纸箱、纸盒、纸袋加工销售为主，纸制品、包装材料、建筑材料、装饰材料、五金、交电、办公用品批发零售为主营业务。公司自成立以来，已建成原料车间、包装车间、仓库车间、办公楼及职工公寓等配套设施，总建筑面积4.5万平方米。引进德国、台湾等地区先进的高速印刷机及其它配套设备，规模化、集约化，自动化的高档纸箱包装生产线，产品主要供应京津唐地区的大型包装用箱企业。项目建成后，可年创产值5亿元，在增值地方经济效益同时，还可有效带动包装、运输等相关行业的发展，具有良好的经济和社会效益。</p><p style="text-indent:2em;">北京圣利来租赁有限公司成立于2011年，经营事业初期提供企业设备租赁服务，中期在于多设据点并引进多样化财务金融服务，并期待能成为中国地区卓越之财务金融公司,以对中国未来经济发展做出贡献。</p><p style="text-indent:2em;">北京圣利丰投资有限公司是经国家工商行政管理部门批准的专业投资机构，于2011年北京注册成立，注册资金为仨仟万元。公司主要经营项目为：投资管理；房地产开发；专业承包；技术开发、技术转让；投资信息咨询（中介除外）；经济信息系咨询；会议服务；承办展览展示；组织文化艺术交流活动（演出除外）；企业形象策划；销售商品房、建筑材料、机械设备、五金、交电、文化体育用品（音像制品除外）。公司成立以来拥有稳定牢固的客户群体，秉承“诚信服务，信誉第一”的经营理念，以努力为客户提供优质、安全、快捷、便利的服务宗旨，在入市短短几年时间里已经成为业界的名牌企业。<br /></p>'),
(25, '爆轰学与爆炸技术', '爆轰学与爆炸技术', '<p>荣誉资质<br /></p>'),
(26, '材料与结构冲击动力学', '材料与结构冲击动力学', '<p>组织机构</p>'),
(27, '毁伤理论与防护技术', '毁伤理论与防护技术', '<p style="text-indent:2em;">董事长简介</p>'),
(28, '爆炸安全理论与评估技术', '爆炸安全理论与评估技术', '<p>联系我们</p><p>&nbsp;</p>');
INSERT INTO `np_group` (`gid`, `type`, `title`, `avatar`, `content`, `order`) VALUES
(1, 29, '朵英贤', '/upload/20140513/13999918785055.jpg', '<p> <span style="font-family:宋体;color:#333333;font-size:14px;line-height:24px;text-indent:28px;background-color:#ffffff;">朵英贤，院士，教授，博导，1932年3月生人。现任北京理工大学兵器科学与技术一级首席专家，北京理工大学学位委员会委员。国防工业出版社顾问。曾任中国兵工学会第二、三届常务理事、第四届名誉理事。</span><span style="font-family:宋体;color:#333333;font-size:14px;line-height:24px;text-indent:28px;background-color:#ffffff;"><br /> &nbsp; &nbsp;长期从事自动武器的研究与开发，为我国自动武器的发展做出了重大贡献。曾获甘肃省先进科技工作者称号，“8910”工程三等功，“976”工程荣誉奖，“9910”工程一等奖。主编《兵器工业科技辞典—轻武器》，获1998年部级科技进步一等奖。近年来，主要从事武器弹药系统延寿理论及可靠性技术。出版著作多部，发表学术论文30余篇。</span><br /></p>', 1),
(2, 29, '徐更光', '/upload/20140513/13999919781630.jpg', '<p><span style="color:#333333;font-size:14px;line-height:24px;text-indent:28px;background-color:#ffffff;"> 徐更光，男，汉族，1932年11月18日生于浙江东阳。1987年12月加入中国共产党，中国工程院院士、教授、博士生导师。1956年毕业于北京工业学院化工系，同年留校任教师，历任实验室主任、研究室主任、北京理工大学力学工程系主任、北京理工大学学术与学位委员会副主任委员、爆炸科学与技术国家重点实验室学术委员会主任。他长期从事炸药与应用技术的教学与科学研究、爆炸能量输出结构与目标力学响应研究、非理想炸药爆炸过程的数值分析方法等基础研究。自1978年以来先后获国家科技进步一等奖等国家级成果奖四项，部委级科技成果奖十余项。所主持的研究集体是北京市、兵器部火炸药行业的先进集体。他本人先后获北京市、国务院国防工办、全国高校科技先进工作者，火炸药行业优秀专家称号。在人民兵工创建60周年之际，中共兵器工业总公司党组授予他“兵器工业功勋奖”荣誉称号。1998年人事部教育部授予他全国教育系统劳动模范，全国模范教师称号。</span><br /></p>', 2),
(3, 32, '宁建国', '/upload/20140513/13999925225671.jpg', '<p style="color:#333333;font-size:14px;line-height:24px;background-color:#ffffff;"> &nbsp; &nbsp;宁建国，博士，教授，博导，1963年3月生人。1992年1月于太原理工大学获得固体力学博士学位。 现任中国力学学会计算力学专业委员会委员，中国力学学会理性力学和力学中的数学方法专业委员会委员，中国力学学会爆炸力学专业委员会计算爆炸力学专业组组长，中国力学学会固体力学专业委员会塑性力学专业组副组长，《计算力学学报》编委，《爆炸与冲击》编委等。</p><span style="color:#333333;font-size:14px;line-height:24px;background-color:#ffffff;"> &nbsp; &nbsp;主要研究领域为爆炸力学、冲击动力学、计算力学与弹药工程。近些年来，密切结合未来国家安全需求，开展了材料动态力学性能、计算爆炸力学和弹药工程等领域的研究工作，在国内外学术刊物上发表学术论文100余篇，其中被SCI和EI收录70余篇。获得省部级科技进步一、二等奖各一项，授权多项国防发明专利及计算机软件著作权登记。2006年获得国家杰出青年基金的资助，2006年评为教育部长江学者奖励计划特聘教授。 </span><p><br /></p>', 3),
(4, 32, '胡更开教授', '/upload/20140513/13999926589726.jpg', '', 4),
(5, 31, '宁建国', '/upload/20140513/13999928779916.jpg', '<p style="color:#333333;font-size:14px;line-height:24px;background-color:#ffffff;"> &nbsp; &nbsp;宁建国，博士，教授，博导，1963年3月生人。1992年1月于太原理工大学获得固体力学博士学位。 现任中国力学学会计算力学专业委员会委员，中国力学学会理性力学和力学中的数学方法专业委员会委员，中国力学学会爆炸力学专业委员会计算爆炸力学专业组组长，中国力学学会固体力学专业委员会塑性力学专业组副组长，《计算力学学报》编委，《爆炸与冲击》编委等。</p><span style="color:#333333;font-size:14px;line-height:24px;background-color:#ffffff;"> &nbsp; &nbsp;主要研究领域为爆炸力学、冲击动力学、计算力学与弹药工程。近些年来，密切结合未来国家安全需求，开展了材料动态力学性能、计算爆炸力学和弹药工程等领域的研究工作，在国内外学术刊物上发表学术论文100余篇，其中被SCI和EI收录70余篇。获得省部级科技进步一、二等奖各一项，授权多项国防发明专利及计算机软件著作权登记。2006年获得国家杰出青年基金的资助，2006年评为教育部长江学者奖励计划特聘教授。</span><p><br /></p>', 5),
(6, 31, '黄风雷', '/upload/20140513/13999930492553.jpg', '<p style="color:#333333;font-size:14px;line-height:24px;background-color:#ffffff;"><span style="font-family:宋体;text-indent:2em;"> &nbsp; &nbsp;黄风雷，博士，教授，博导，1965年6月生人，1991年获工学博士学位，教育部“长江学者”奖励计划特聘教授，国务院学位委员会兵器科学与技术学科评议组成员，教育部首批创新团队“爆炸毁伤技术”学术创新团队带头人，国家安全重大基础研究项目技术首席专家。</span></p><p style="color:#333333;font-size:14px;line-height:24px;background-color:#ffffff;"><span style="font-family:宋体;text-indent:2em;">现任《兵工学报》副主编，中国力学学会爆炸力学专业委员会副主任委员。<br /> &nbsp; &nbsp;发表学术论文122篇，出版学术专著五部，获国防科技进步一等奖一项，部级科技进步二等奖四项，获专利授权5项。</span></p><p><br /></p>', 6),
